mapper.select:
select a.`id`, a.`client_id`, a.`topic`, a.`content`, a.`create_time` b.`gateway_id`,b.`name` as clientName,b.`last_link_time`, c.`name` as gatewayName,c.`address` from iot_warning_result a left join iot_client b on a.`client_id` = b.`id` left join iot_gateway c on b.`gateway_id` = c.`id`
a.`id` = #{id}
and a.`name` = #{name}
and a.`client_id` = #{clientId}
and topic like concat(#{topic},"%")
and date_format(a.`create_time`,'%Y-%m-%d') between date_format(#{beginTime},'%Y-%m-%d') and date_format(#{endTime},'%Y-%m-%d')
limit 1

select
    a.`id`, a.`client_id`, a.`topic`, a.`content`, a.`create_time`
    b.`gateway_id`,b.`name` as clientName,b.`last_link_time`,
    c.`name` as gatewayName,c.`address`
from iot_warning_result a left join iot_client b on a.`client_id` = b.`id` left join iot_gateway c on b.`gateway_id` = c.`id`


        a.`id` = #{id}


        and a.`name` = #{name}


        and a.`client_id` = #{clientId}


        and topic like concat(#{topic},"%")


        and date_format(a.`create_time`,'%Y-%m-%d') between date_format(#{beginTime},'%Y-%m-%d')  and date_format(#{endTime},'%Y-%m-%d')


order by a.`id` desc
limit #{startLine},#{endLine}

select
    count(*)
from iot_warning_result a


        a.`id` = #{id}


        and a.`name` = #{name}


        and a.`client_id` = #{clientId}


        and topic like concat(#{topic},"%")


        and date_format(a.`create_time`,'%Y-%m-%d') between date_format(#{beginTime},'%Y-%m-%d')  and date_format(#{endTime},'%Y-%m-%d')
